definition.script:
pipeline {
    agent any

    stages {
        stage('Build') {
            steps {
                // git pull de la rama master del proyecto
                bat "git pull origin master"

                // compilar el gradle
                bat "gradle build"

                // ejecutar el jar
                bat "gradle jar"
            }
        }
        stage('Test') {
            steps {
                // ejecutar con gradle la bateriÃÅa de pruebas y generar un reporte de resultado de las mismas
                bat "gradle test"
            }
        }
        stage('Validate') {
            steps {
                bat "gradle check"
            }
        }
        stage('Deploy') {
            steps {
                bat "mvnw spring-boot:run"
            }
        }
    }
}
